mapper.insert:
/* 축사 정보 삽입 - 초기에는 상태 정보 추가 안함 */
INSERT INTO ptj_sub1.barns
(
m_id
)
VALUES
(
#{m_id}
);

mapper.update:
UPDATE ptj_sub1.barns
SET temperature =#{temperature}, humidity=#{humidity}, air_condition=#{air_condition}, time=#{time}
WHERE b_id =#{b_id}

UPDATE ptj_sub1.barns
SET count = count+1
WHERE b_id =#{b_id}
