mapper.delete:
DELETE
FROM ptj_sub1.boards
WHERE bno =#{bno} and m_id =#{m_id}

mapper.update:
UPDATE ptj_sub1.boards
SET title =#{title}, content=#{content}
WHERE bno =#{bno}

UPDATE ptj_sub1.boards
SET viewcnt = viewcnt+1
WHERE bno =#{bno}

UPDATE ptj_sub1.boards
SET replycnt = replycnt+1
WHERE bno =#{bno}
