mapper.insert:
		/* ls_bs 테이블 */
		INSERT INTO ptj_sub1.ls_bs
		(
		ls_id,
        b_id,
        location
		)
		VALUES
		(
		#{ls_id},
        #{b_id},
        #{location}
		);

mapper.delete:
DELETE
FROM ptj_sub1.ls_bs
WHERE ls_id =#{ls_id} AND b_id =#{b_id}

mapper.update:
 UPDATE ptj_sub1.ls_bs
 SET location =#{location}
WHERE ls_id =#{ls_id} AND b_id =#{b_id}
